    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        // TODO add your handling code here:
        if(txtRut.getText().trim().length() < 1)
        {
            JOptionPane.showMessageDialog(this, "Falta especificar el rut");
            txtRut.requestFocus();
        }
        else 
        {
            CPersona controlador = new CPersona();
            Boolean respuesta = controlador.eliminar(txtRut.getText());
            if(respuesta)
            {
                JOptionPane.showMessageDialog(this, "El Rut fue eliminado");
                btnLimpiar.doClick();
                btnListar.doClick();
            }
            else
                JOptionPane.showMessageDialog(this, "Error al eliminar el registro");
        }
    }//GEN-LAST:event_btnEliminarActionPerformed

    private void btnListarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnListarActionPerformed
        // TODO add your handling code here:
        
        CPersona controlador = new CPersona();
        ArrayList<Persona> listado = controlador.buscarTodos();
        
        if(listado.size() == 0)
        {
            JOptionPane.showMessageDialog(this, "No hay datos registrados");
            return;
        }
        
        DefaultTableModel modelo = (DefaultTableModel) grilla.getModel();
        modelo.setRowCount(0);// limpiar grilla
        for (Persona persona : listado) {
            modelo.addRow(new Object[]{
                persona.getRut(),
                persona.getNombre(),
                persona.getApellido(),
                persona.getDireccion()
            });
            
        }
            
        
    }//GEN-LAST:event_btnListarActionPerformed

